<div style="display: flex;justify-content: center;align-items: center;height: 500px;">
    <form action="http://localhost/ecommerce/getFormUser" method ="post" style="background-color: bisque;padding: 10px;margin: 10px;border-radius: 10px;display: flex;flex-direction: column;align-items: center;">
        <input name = 'name' placeholder='name' style="margin: 10px;padding: 5px;border-radius: 10px;border: none;text-align: center;">
        <input name = 'family' placeholder='family' style="margin: 10px;padding: 5px;border-radius: 10px;border: none;text-align: center;">
        <input name = 'phonNumber' placeholder='phonNumber' style="margin: 10px;padding: 5px;border-radius: 10px;border: none;text-align: center;">
        <button style="margin: 10px;padding: 5px;border-radius: 10px;border: none;background: none;">send</button>
    </form>
</div>